  def visits(str: String): Int = {
    str.foldLeft(((0, 0), Set((0, 0)))) {
      case ((curPos, visited), '^') =>
        val newPos = (curPos._1, curPos._2 - 1)
        (newPos, visited + newPos)
      case ((curPos, visited), '<') =>
        val newPos = (curPos._1 - 1, curPos._2)
        (newPos, visited + newPos)
      case ((curPos, visited), '>') =>
        val newPos = (curPos._1 + 1, curPos._2)
        (newPos, visited + newPos)
      case ((curPos, visited), 'v') =>
        val newPos = (curPos._1, curPos._2 + 1)
        (newPos, visited + newPos)
    }._2.size
  }

  def visitsRobo(str: String): Int = {
    val seed = ((0, 0), (0, 0), Set((0, 0)), Set((0, 0)))
    val result = str.grouped(2).foldLeft(seed) {
      case ((roboPos: (Int, Int), santaPos: (Int, Int), roboSanta: Set[(Int, Int)], santa: Set[(Int, Int)]), instructions) =>
        val newRobo = instructions(0) match {
          case '^' =>
            val newPos = (roboPos._1, roboPos._2 - 1)
            (newPos, roboSanta + newPos)
          case 'v' =>
            val newPos = (roboPos._1, roboPos._2 + 1)
            (newPos, roboSanta + newPos)
          case '<' =>
            val newPos = (roboPos._1 - 1, roboPos._2)
            (newPos, roboSanta + newPos)
          case '>' =>
            val newPos = (roboPos._1 + 1, roboPos._2)
            (newPos, roboSanta + newPos)
        }
        val newSanta = instructions(1) match {
          case '^' =>
            val newPos = (santaPos._1, santaPos._2 - 1)
            (newPos, santa + newPos)
          case 'v' =>
            val newPos = (santaPos._1, santaPos._2 + 1)
            (newPos, santa + newPos)
          case '<' =>
            val newPos = (santaPos._1 - 1, santaPos._2)
            (newPos, santa + newPos)
          case '>' =>
            val newPos = (santaPos._1 + 1, santaPos._2)
            (newPos, santa + newPos)
        }
        (newRobo._1, newSanta._1, newRobo._2, newSanta._2)
    }
    (result._3 ++ result._4).size
  }

  println(visits(input))

  println(visitsRobo(input))
}
